import React, { useEffect, useState } from "react";
import "./News.scss"

const News = ({category}) => {
  const [mynews, setMyNews] = useState([]);

  const fetchData = async () => {
    let resonse = await fetch(
      `https://newsapi.org/v2/top-headlines?country=us&category=${category}&apiKey=e8bcb5454d444bdbba06ed45e8c8a3cb`
    );
    let data = await resonse.json();
    setMyNews(data.articles);
    const [currentPage,setCurrentPage] =useState(1);
  const recordsPerPage = 5;
  const lastIndex = currentPage * recordsPerPage;
  const firstIndex = lastIndex - recordsPerPage;
  const records = data.slice(firstIndex,lastIndex);
  const nPage = Math.ceil(data.length/ recordsPerPage);
  const numbers = [...Array(nPage+1).keys()].slice(1);
  };
 
  useEffect(() => {
    fetchData();
  }, [category]);

  

  return (
    <>
    <h1 className="text-center my-3">Latest <span className="badge bg-danger">News</span></h1>
          <div className="mainDiv">
      {mynews.map((ele) => {
        console.log(ele)
        return (
          <>
            <div class = "card " >
              <div className="conatiner">
              <img src={ele.urlToImage}  alt="" />
              <div className="overlay"></div>
              </div>
              <div class="card-content overlay">
                <h2>
                {ele.author == "" ? "Janelle Ash" : ele.author}
                </h2>
                <p>
                  {ele.title}
                </p>
                <a href={ele.url} class="button">
                  Find out more 
                  <span class="material-symbols-outlined">
                   {'->'}
                  </span>
                </a>
              </div>
            </div>
          </>
        );
    })}
    <nav>
      <ul className="pagination">
      <li className="page-item"><a href ="#" className="page-link" onClick={prePage}>Prev</a></li>
      {
        numbers.map((n,i)=>{
          <li className={`page-item ${currentPage === n ? 'active' : ''}`} key={i}><a href="#" className="page-item" onClick={()=> changeCPage(n)}>{n}</a></li>
        })
      }
      <li className="page-item"><a href ="#" className="page-link" onClick={nextPage}>Next</a></li>

      </ul>
    </nav>
    </div>
    </>
  );
  function prePage() { 
    if(currentPage!== 1){
      setCurrentPage(currentPage-1);
    }
  }
  function nextPage() { 
    if(currentPage!== nPage){
      setCurrentPage(currentPage+1);
    }
  }
  function changeCPage(id){
    setCurrentPage(id);
  }
};

export default News;
