import React, { useEffect, useState } from "react";
import "./News.scss";

const News = ({ category, searchTerm }) => {
  const [mynews, setMyNews] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const recordsPerPage = 6;

  const fetchData = async () => {
    setLoading(true);
    setError(null);
    try {
      let url = `https://newsapi.org/v2/top-headlines?country=us&category=${category}&apiKey=e8bcb5454d444bdbba06ed45e8c8a3cb`;
      if (searchTerm) {
        url = `https://newsapi.org/v2/everything?q=${searchTerm}&apiKey=e8bcb5454d444bdbba06ed45e8c8a3cb`;
      }
      let response = await fetch(url);
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      setMyNews(data.articles);
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [category, searchTerm]);

  const lastIndex = currentPage * recordsPerPage;
  const firstIndex = lastIndex - recordsPerPage;
  const records = mynews.slice(firstIndex, lastIndex);
  const nPage = Math.ceil(mynews.length / recordsPerPage);
  const numbers = [...Array(nPage + 1).keys()].slice(1);

  const prePage = () => {
    if (currentPage !== 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const nextPage = () => {
    if (currentPage !== nPage) {
      setCurrentPage(currentPage + 1);
    }
  };

  const changeCPage = (id) => {
    setCurrentPage(id);
  };

  if (loading) {
    return <div className="text-center my-3">Loading...</div>;
  }

  if (error) {
    return <div className="text-center my-3">Error: {error.message}</div>;
  }

  return (
    <>
      <h1 className="text-center my-3">
        Latest <span className="badge bg-danger">News</span>
      </h1>
      <div className="mainDiv">
        <div className="main">
          {records.map((ele) => (
            <div className="card" key={ele.url}>
              <div className="container">
                <img
                  src={ele.urlToImage || "https://via.placeholder.com/150"}
                  alt=""
                  onError={(e) => (e.target.src = "https://via.placeholder.com/150")}
                />
                <div className="overlay"></div>
              </div>
              <div className="card-content overlay wr">
                <h2>{ele.author === "" ? "Janelle Ash" : ele.author}</h2>
                <p>{ele.title}</p>
                <a href={ele.url} className="button">
                  Find out more
                  <span className="material-symbols-outlined">{"->"}</span>
                </a>
              </div>
            </div>
          ))}
        </div>
        <div className="center">
          <ul className="pagination">
            <li className="page-item">
              <a href="#" className="page-link" onClick={prePage}>
                Prev
              </a>
            </li>
            {numbers.map((n) => (
              <li
                className={`page-item ${currentPage === n ? "active" : ""}`}
                key={n}
              >
                <a
                  href="#"
                  className="page-link"
                  onClick={() => changeCPage(n)}
                >
                  {n}
                </a>
              </li>
            ))}
            <li className="page-item">
              <a href="#" className="page-link" onClick={nextPage}>
                Next
              </a>
            </li>
          </ul>
        </div>
      </div>
    </>
  );
};

export default News;
