import React, { useEffect, useState } from "react";
import "./News.scss";

const News = ({ category }) => {
  const [mynews, setMyNews] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState("");
  const recordsPerPage = 6;

  const fetchData = async () => {
    try {
      let response = await fetch(
        `https://newsapi.org/v2/top-headlines?country=us&category=${category}&apiKey=e8bcb5454d444bdbba06ed45e8c8a3cb`
      );
      const data = await response.json();
      setMyNews(data.articles);
    } catch (error) {
      console.error("Error fetching data:", error);
      // Handle error fetching data (e.g., show error message)
    }
  };

  useEffect(() => {
    fetchData();
  }, [category]);

  const lastIndex = currentPage * recordsPerPage;
  const firstIndex = lastIndex - recordsPerPage;
  const filteredNews = mynews.filter((news) =>
    news.title.toLowerCase().includes(searchTerm.toLowerCase())
  );
  const records = filteredNews.slice(firstIndex, lastIndex);
  const nPage = Math.ceil(filteredNews.length / recordsPerPage);
  const numbers = [...Array(nPage + 1).keys()].slice(1);

  const prePage = () => {
    if (currentPage !== 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const nextPage = () => {
    if (currentPage !== nPage) {
      setCurrentPage(currentPage + 1);
    }
  };

  const changeCPage = (id) => {
    setCurrentPage(id);
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
    setCurrentPage(1); // Reset current page when search term changes
  };

  return (
    <>
      <h1 className="text-center my-3">
        Latest <span className="badge bg-danger">News</span>
      </h1>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search by keyword..."
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>
      <div className="mainDiv">
        <div className="main">
          {records.map((ele) => (
            <div className="card" key={ele.url}>
              <div className="container">
                <img
                  src={ele.urlToImage}
                  alt=""
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src =
                      "https://via.placeholder.com/400x200?text=Image+Not+Found";
                    e.target.closest(".container").classList.add("error");
                  }}
                />
                <div className="overlay"></div>
              </div>
              <div className="card-content overlay">
                <h2>{ele.author === "" ? "Janelle Ash" : ele.author}</h2>
                <p>{ele.title}</p>
                <a href={ele.url} className="button">
                  Find out more
                  <span className="material-symbols-outlined">{"->"}</span>
                </a>
              </div>
            </div>
          ))}
        </div>
        <div className="center">
          <ul className="pagination">
            <li className="page-item">
              <a href="#" className="page-link" onClick={prePage}>
                Prev
              </a>
            </li>
            {numbers.map((n) => (
              <li
                className={`page-item ${currentPage === n ? "active" : ""}`}
                key={n}
              >
                <a href="#" className="page-link" onClick={() => changeCPage(n)}>
                  {n}
                </a>
              </li>
            ))}
            <li className="page-item">
              <a href="#" className="page-link" onClick={nextPage}>
                Next
              </a>
            </li>
          </ul>
        </div>
      </div>
    </>
  );
};

export default News;
