import React, { useState } from 'react';
import './Navbar.css';

const Navbar = ({ setCategory }) => {
  const [isActive, setIsActive] = useState({
    general: false,
    technology: false,
    business: false,
    health: false,
    science: false,
    sports: false,
    entertainment: false,
    demo: false, // Example demo link
  });

  const handleClick = (category) => {
    setIsActive({ ...isActive, [category]: true });
    setCategory(category);
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <div className="container-fluid">
        <a className="navbar-brand" href="#"><b>My News-App</b></a>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <div
                className={`nav-link ${isActive.general ? 'active' : ''}`}
                onClick={() => handleClick('general')}
              >
                General
              </div>
            </li>
            <li className="nav-item">
              <div
                className={`nav-link ${isActive.technology ? 'active' : ''}`}
                onClick={() => handleClick('technology')}
              >
                Technology
              </div>
            </li>
            <li className="nav-item">
              <div
                className={`nav-link ${isActive.business ? 'active' : ''}`}
                onClick={() => handleClick('business')}
              >
                Business
              </div>
            </li>
            <li className="nav-item">
              <div
                className={`nav-link ${isActive.health ? 'active' : ''}`}
                onClick={() => handleClick('health')}
              >
                Health
              </div>
            </li>
            <li className="nav-item">
              <div
                className={`nav-link ${isActive.science ? 'active' : ''}`}
                onClick={() => handleClick('science')}
              >
                Science
              </div>
            </li>
            <li className="nav-item">
              <div
                className={`nav-link ${isActive.sports ? 'active' : ''}`}
                onClick={() => handleClick('sports')}
              >
                Sports
              </div>
            </li>
            <li className="nav-item">
              <div
                className={`nav-link ${isActive.entertainment ? 'active' : ''}`}
                onClick={() => handleClick('entertainment')}
              >
                Entertainment
              </div>
            </li>
            <li className="nav-item">
              <div className="nav-link">Demo</div>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
